import Head from '../../components/head';
import Header from '../../components/header'
import {useState , useEffect} from 'react';
import {Spin } from 'antd';
import jwt from 'jsonwebtoken';
import PublicProfile from './Components/publicProfile';

const Profile = ({props , authUsr})=>{

    const [userid,setData] = useState();
    const [loading,setLoading] = useState(true);

    useEffect(()=>{
        // if (authUsr !== undefined ) {
        //     setData(authUsr);
        //     setLoading(false);
        // }else{
        //     console.log(props.data.error)
        //     const location = "/Auth/Signin/?ref=tokenexpired&location="+window.location.href;
        //     window.location.replace(location);
        // }
        console.log(props,authUsr);
    },[])
    if (loading ) {
        
        return(
        <>
                <Head title="Loading"/>
                <div style={{position : 'fixed',top : '50%',left:'50%',marginTop :'-50px',marginLeft:'-50px'}}>
                    <Spin size="large" />
                </div>
        </>
        )
    }else{
        return(
            <div className="body">
                    <Header/>
                    {props.data === undefined ? <Head title="Loading"/> :''}
                    <div style={{marginTop :'8%'}}>
                        { props.data !== undefined && (
                            <PublicProfile profile={props.data.profile[0]} userid={userid} />
                        )}  
                    </div>
            </div>
        )        
    }
    
}


Profile.getInitialProps = async (ctx) => {

    const Authres = await fetch('http://localhost:9000/profiles/myprofile',{credentials : 'include'});
    const Authdata = await Authres.json();
    const usrAuth = Authdata.profile._id ;
    if (ctx.query._id !== 'Myprofile') {
        const url = "http://localhost:9000/profiles/profile?userid="+ctx.query._id ;
        const res = await fetch(url);
        Authdata = await res.json();
    } 
        
    return{
            props : {
                Authdata
            },
            authUsr : usrAuth 
    }
  }

export default Profile ;
