import Head from '../../components/head';
import {useState , useEffect} from 'react';
import Spin from 'antd';
import jwt from 'jsonwebtoken';
import MyProfile from './Components/myProfile';
import PublicProfile from './Components/publicProfile';

const Profile = ({data})=>{

    const profile = data.profile[0];
    
    const [userid,setData] = useState();
    const [loading,setLoading] = useState(true);

    useEffect(()=>{
        const token = window.localStorage.getItem("Tokens");
        jwt.verify(token,"secret",function (err , decoded) {
            if (!err) {
                setData(decoded.userId);
                setLoading(false);
            }else
            {
                console.log(err);
                const location = "/Auth/Signin/?ref=tokenexpired&location="+window.location.href;
                window.location.replace(location);
            }
        });
        
    },[])
    if (loading) {
        
        return(
        <>
                <Head title="Loading"/>
                <div style={{position : 'fixed',top : '50%',left:'50%',marginTop :'-50px',marginLeft:'-50px'}}>
                    <Spin size="large" />
                </div>
        </>
        )
    }else{
        return(
            <div>
                    {userid === profile._id &&(
                        <MyProfile profile={profile}/>
                    )}
                    {userid !== profile._id &&(
                        <PublicProfile profile={profile}/>
                    )}
            </div>
        )        
    }
    
}


export async function getStaticPaths(){
    const res = await fetch("http://localhost:9000/profiles/");
    const data = await res.json();
    var paths = [];
    data.response.profiles.map(profile=>{
        paths.push({params : {
            _id : profile._id
        }})
    })
    
    return{
        paths ,
        fallback : false
    }
}
export async function getStaticProps({ params }){

    const url = "http://localhost:9000/profiles/profile?userid="+params._id ;
    const res = await fetch(url);
    const data = await res.json();
    return{
       props : {
           data
       }
    }
}


export default Profile ;
