// Dependencies ------------------------

import {useState , useEffect} from 'react';
import { Menu } from 'antd' ;
import '../../../styles/ant-picker.scss';


// Components -------------------------
import Icon from '@ant-design/icons';
import HomeSvg from './Svg/HomeSvg';
import AboutSvg from './Svg/AboutSvg';
import ClientSvg from './Svg/ClientSvg';
import '../Sass/fullProfile.scss';
import LikeButton from '../../../components/LikeButton';

// -------------------------------------
const ProfileBody = ({profile})=>{

    console.log(profile);
    // States ---------------------------------------
    const [current , setCurrent] = useState('Acceuil');
    const  [checkbox,setCheck] = useState(false);

    // Icons -----------------------------------------
    const HomeIcon = props => <Icon component={HomeSvg} {...props} />;
    const AboutIcon = props => <Icon component={AboutSvg} {...props} />;
    const ClientsIcon = props => <Icon component={ClientSvg} {...props} />;

    // Handlers -------------------------------------
    const handleLike = ()=>{
        setCheck(!checkbox);
    }
    const handleClick = e =>{
        setCurrent(e.key);
    }

    return(
        <div className="Profile">
            <div className="ProfileHead">
                <div className="ProfileImg">
                    <img src="https://pbs.twimg.com/profile_images/1262280175691104258/dM1dOVxY_400x400.jpg" />
                </div>
                <div className="ProfileInfo">
                    <h3>Noumane industries</h3>
                    <p><i className="fa fa-user" aria-hidden="true" style={{marginRight :'10px'}}></i><span>Noumane Agouzil</span> | <span>CTO</span></p>
                    <span className="Likes"><i class="fa fa-heart" aria-hidden="true" style={{marginRight :'5px'}}></i> 200 J'aime</span>
                </div>
            </div>
            <div className="ProfileBody">
                <Menu onClick={handleClick} selectedKeys={current} mode="horizontal" style={{marginTop :'3%'}}>
                    <Menu.Item key="Acceuil" icon={<HomeIcon />} >
                        Acceuil
                    </Menu.Item>
                    <Menu.Item key="propos" icon={<AboutIcon />}>
                        A propos
                    </Menu.Item>
                    <Menu.Item key="clients" icon={<ClientsIcon />}>
                        Clients
                    </Menu.Item>
                </Menu>
                <div className="MenuContent">
                    {current === "Acceuil" &&(
                        <>
                            <div className="presentation">
                                <h2>Presentation de l'entreprise</h2>
                                <p>Tesla, Inc., initialement appelé Tesla Motors jusqu'au 1er février 20174, est un constructeur automobile de voitures électriques dont le siège social se situe à Palo Alto, en Californie, dans la Silicon Valley, aux États-Unis. L'entreprise a été fondée en 2003 par Martin Eberhard et Marc Tarpenning et son personnage principal ainsi qu'actuel patron est Elon Musk qui a fait de l'entreprise un constructeur d'automobiles électriques grand public. </p>
                            </div>
                            <div className="Horraire">
                                <h2>Horraire</h2>
                                <div className="TimeTable">
                                    <div className="Block">
                                         <ul>
                                             <li className="Today">Lundi</li>
                                             <li>Mardi</li>
                                             <li>Mercredi</li>
                                         </ul>
                                         <ul>
                                             <li className="Today">09:00-18:00</li>
                                             <li>09:00-18:00</li>
                                             <li>09:00-18:00</li>
                                         </ul>
                                    </div>
                                    <div className="Block">
                                        <ul>
                                             <li>Jeudi</li>
                                             <li>Vendredi</li>
                                             <li>Samedi</li>
                                        </ul>
                                        <ul>
                                             <li>09:00-18:00</li>
                                             <li>09:00-18:00</li>
                                             <li>09:00-18:00</li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </>
                    )}
                </div>
                <div className="MenuFooter">
                    <div className="like">
                        <LikeButton checkbox={checkbox} handleLike={handleLike} /><span className={checkbox ? "active" : ""}>J'aime</span>

                    </div>
                    
                    <div className="share">
                    <i class="fa fa-share-alt" aria-hidden="true" style={{marginRight :'5px'}}></i>Partager
                    </div>

                    
                </div>
            </div>
        </div>
    )
}
export default ProfileBody ;
