import Head from '../../components/head';
import Header from '../../components/header'
import {useState , useEffect} from 'react';
import {Spin } from 'antd';
import jwt from 'jsonwebtoken';
import PublicProfile from './Components/publicProfile';
import { VerticalRightOutlined } from '@ant-design/icons';

const Profile = ({props , AuthId})=>{

    const [userid,setData] = useState();
    const [loading,setLoading] = useState(true);

    useEffect(()=>{
        if (props.data.profile !== undefined ) {
            setData(AuthId);
            setLoading(false);
        }else{
            console.log(props.data.error)
            const location = "/Auth/Signin/?ref=tokenexpired&location="+window.location.href;
            window.location.replace(location);
        }
        console.log(props , AuthId);
    },[])
    if (loading ) {
        
        return(
        <>
                <Head title="Loading"/>
                <div style={{position : 'fixed',top : '50%',left:'50%',marginTop :'-50px',marginLeft:'-50px'}}>
                    <Spin size="large" />
                </div>
        </>
        )
    }else{
        return(
            <div className="body">
                    <Header/>
                    {props.data === undefined ? <Head title="Loading"/> :''}
                    <div style={{marginTop :'8%'}}>
                        { props.data !== undefined && (
                            <PublicProfile profile={props.data.profile} userid={userid} />
                        )}  
                    </div>
            </div>
        )        
    }
    
}


Profile.getInitialProps = async (ctx) => {

    const cookie = ctx.req.headers.cookie ;
    const AuthId='';
    const res = await fetch('http://localhost:9000/profiles/myprofile',{
        headers : {
            cookie
        }
    });
    var data = await res.json();
    data.profile !== undefined ? AuthId = data.profile._id  : "" ;

    if (ctx.query._id !== 'Myprofile' && ctx.query._id !== AuthId && data.profile !== undefined ) {
        const url = "http://localhost:9000/profiles/profile?userid="+ctx.query._id ;
        const res = await fetch(url,{
            headers : {
                cookie
            }
        }); 
        data = await res.json();
    }
    return{
        props : {
            data
        },
        AuthId : AuthId
    }
  }

export default Profile ;
