// dependecies 
import React, { useState } from 'react';
import { AutoComplete } from 'antd';
import axios from 'axios';
// Components and styles imports

import Charach from './svg/NerdCharact';
import '../styles/bodySection1.scss';
import '../pages/Auth/Sass/ant-picker.scss';
import '../styles/AutoComplete.scss';
// ----------------------------------------------------

const Body_section1 = ()=>{
    
    const { Option } = AutoComplete;
    const [nameText , setNametext]= useState("");
    const [domaineText , setdomainetext]= useState("");
    const [cityText , setcitytext]= useState("");
    const [nameOptions, setOptions] = useState([]);
    const [DomaineOptions, setDomaineOptions] = useState([]);
    const [CityOptions, setCityOptions] = useState([]);
    const [Names, setNames] = useState([]);
    const Domaines = [{companyname : "electrcite"},{companyname : "electronic"},{companyname : "web design"},{companyname : "web developpement"}];
    const Cities = [{value : "casablanca"},{value : "rabat"},{value : "meknes"},{value : "marrakesh"}];

    const onSearchName = searchText => {
      setNametext(searchText);
      /*setdomainetext();
      setcitytext()*/
      const Url = 'http://localhost:9000/profiles/search?companyname='+nameText+'&domaine='+domaineText+'city='+cityText ;
      axios.get(Url).then(({data}) =>{
          if (data !== undefined) {
              setNames(data.profile);
              if (Names.length !== 0) {
                setOptions(!searchText ? [] : Names);
              }
        }
      }).catch(err => console.log(err));
    };
    const onSearchDomaine = searchText => {
      setDomaineOptions(
        !searchText ? [] : Domaines.filter(el => el.companyname.includes(searchText.toLowerCase()))
      );
    };
    const onSearchCities = searchText => {
      console.log(searchText);
      setCityOptions(
        !searchText ? [] : Cities.filter(el => el.value.includes(searchText.toLowerCase()))
      );
    };
    
    const onSelect = data => {
      console.log('onSelect', data);
    };
    return (
      <div className="section1_container">
        <div className="Left_segment">
          <h1>Recherchez des entreprises et organisez une réunion</h1>
          <div className="search_Container">
            <div className="inputContainer">
              <i aria-hidden className="fa fa-id-card icon"></i>
              {/* <input className="searchInput" type="text" placeholder="Société/Personne" /> */}
              <AutoComplete
                className="searchInput"
                onSelect={onSelect}
                onSearch={onSearchName}
                placeholder="Société/Personne"
              >
                 {nameOptions.map(name =>(
                   
                  <Option key={name.companyname} value={name.companyname}>
                    {name.companyname}
                  </Option>
                ))}
              </AutoComplete>
            </div>
            <div className="inputContainer">
              <i aria-hidden className="fa fa-globe icon"></i>
              {/* <input className="searchInput" type="text" placeholder="Domaine " /> */}
              <AutoComplete className="searchInput" onSelect={onSelect} onSearch={onSearchDomaine} placeholder="Domaine">
                {DomaineOptions.map(domaine =>(
                  <Option key={domaine.companyname} value={domaine.companyname}>
                    {domaine.companyname}
                  </Option>
                ))}
              </AutoComplete>
            </div>
            <div className="inputContainer">
              <i aria-hidden className="fa fa-map-marker icon"></i>
              {/* <input className="searchInput" type="text" placeholder="Ville" /> */}
              <AutoComplete
              className="searchInput"
                options={CityOptions}
                onSelect={onSelect}
                onSearch={onSearchCities}
                placeholder="Ville"
              />
            </div>

            <button className="searchButton" type="button">
              <i aria-hidden className="fa fa-search"></i>
            </button>
            
          </div>
        </div>
        <div className="right_segment">
          <Charach />
        </div>
      </div>
    );
}

export default Body_section1 ;
