import Link from 'next/link';
import {useState} from 'react';
const FormComponent = () => {
    var data = {
      type: "Société",
    };
    var [current,setCurrent] = useState(0);
    var [userData , setData] = useState(data);

    const handleChange = (e)=>{
        setData({type : e.target.value});
        console.log(userData);
    }
    return (
      <div className="FormContainer">
        <h1>Inscrivez-vous</h1>
        {current == 0 && (
          <div className="part 1">
            <h2>Choisissez le type de votre compte</h2>
            <form onSubmit={(e) => e.preventDefault()}>
              <select className="select" value={userData.type} onChange={handleChange}>
                <option value="Société">Société</option>
                <option value="Client">Client</option>
              </select>
              <div className="buttonContainer">
                <input
                  className="SignButton next"
                  type="submit"
                  value="Suivant"
                  onClick={() => setCurrent(1)}
                />
              </div>
            </form>
          </div>
        )}
        {current == 1 && (
          <div className="part 1">
            <form onSubmit={(e) => e.preventDefault()}>
              {/* <input className="input" type="text" placeholder="Nom de société"/>
              <div className="multipleInput one">
                <input className="input controlled " type="text" placeholder="Titre"/>
                <input className="input controlled" type="text" placeholder="Domaine"/>
              </div> */}
              <div className="multipleInput two">
                <select className="small">
                    <option value="male" selected>Mr</option>
                    <option value="female">Mme</option>
                </select>
                <input className="input controlled user" type="text" placeholder="Nom"/>
                <input className="input controlled user" type="text" placeholder="Prenom"/>
              </div>
                <input className="input" type="text" placeholder="Email"/>
              {/* <div className="multipleInput three"> */}
                <input className="input   " type="password" placeholder="Mot de passe"/>
                <input className="input  " type="password" placeholder="Confirmez votre mot de passe"/>
              {/* </div> */}
              
              <div className="buttonContainer">
                <input
                  className="SignButton next"
                  type="submit"
                  value="Suivant"
                  onClick={() => setCurrent(2)}
                />
                <input
                  className="Back"
                  type="submit"
                  value="Precedent"
                  onClick={() => setCurrent(0)}
                />
              </div>
            </form>
          </div>
        )}

        <div className="FormFooter">
          <span>Déjà membre ?</span>
          <Link href="Signin">Connectez-vous</Link>
        </div>
      </div>
    );
}
export default FormComponent;
