import {useEffect , useState} from 'react'
import jwt from 'jsonwebtoken';

const  CheckAuth = ()=>{
            const token = window.localStorage.getItem("Tokens");
            jwt.verify(token,"secret",function (err , decoded) {
                if (!err) {
                    return decoded.userId
                }else
                {
                    return err
                }
            });

}

export default CheckAuth ;
