import {useState , useEffect} from 'react';
import { Menu } from 'antd' ;
import '../../../styles/ant-picker.scss';
import Icon from '@ant-design/icons';
import HomeSvg from './Svg/HomeSvg';
import AboutSvg from './Svg/AboutSvg';
import ClientSvg from './Svg/ClientSvg';
import '../Sass/fullProfile.scss';
const ProfileBody = ()=>{

    // States ---------------------------------------
    const [current , setCurrent] = useState('Acceuil');
    const HomeIcon = props => <Icon component={HomeSvg} {...props} />;
    const AboutIcon = props => <Icon component={AboutSvg} {...props} />;
    const ClientsIcon = props => <Icon component={ClientSvg} {...props} />;
    // Handlers -------------------------------------

    const handleClick = e =>{
        setCurrent(e.key);
    }

    return(
        <div className="Profile">
            <div className="ProfileHead">
                <div className="ProfileImg">
                    <img src="https://pbs.twimg.com/profile_images/1262280175691104258/dM1dOVxY_400x400.jpg" />
                </div>
                <div className="ProfileInfo">
                    <h3>Noumane industries</h3>
                    <p><i className="fa fa-user" aria-hidden="true" style={{marginRight :'5px'}}></i><span>Noumane Agouzil</span> | <span>CTO</span></p>
                    <span className="Likes"><i class="fa fa-heart" aria-hidden="true" style={{marginRight :'5px'}}></i> 200 J'aime</span>
                </div>
            </div>
            <div className="ProfileBody">
                <Menu onClick={handleClick} selectedKeys={current} mode="horizontal" style={{textAlign : 'center'}}>
                    <Menu.Item key="Acceuil" icon={<HomeIcon />} >
                        Acceuil
                    </Menu.Item>
                    <Menu.Item key="propos" icon={<AboutIcon />}>
                        A propos
                    </Menu.Item>
                    <Menu.Item key="clients" icon={<ClientsIcon />}>
                        Clients
                    </Menu.Item>
                </Menu>
                <div className="MenuContent">

                </div>
                <div className="MenuFooter">

                </div>
            </div>
        </div>
    )
}
export default ProfileBody ;
