import Modal from 'antd/lib/modal/Modal';
import {useState} from 'react';
import moment from 'moment';
import { Button } from 'antd';
const Scheduler =  (StartHour , endtHour , length )=>{
    const results = [];
     for (let i = 0; i <= parseInt(length) ; i++) {
        const string  =  StartHour.format('HH:mm')+"-"+StartHour.add(30,'minutes').format('HH:mm') ;

        // Break betweeen meetings
         results.push(string);
        if (Math.abs(moment.duration(StartHour.diff(endtHour)).asMinutes()) <= 30 ) {
            break ;
        }
        
         StartHour.add(15,'minutes');
    }
    return(results);
}

const DatePicker = (string) =>
{
    const day = new Date(string);
    const Days = ['Dimanche','Lundi','Mardi','Mercredi','Jeudi','Vendredi','Samedi'];
    const Months = ['Janvier','Février','Mars','Avril','Mai','Juin','Juillet','Aout','Septembre','Octobre','Novembre','Decembre'];
    const DisplayString = [Days[day.getDay()],day.getDate(),Months[day.getMonth()+1]];

    return DisplayString;
}

const calendar = ()=>{

    //Data from database 
    const date = new Date();

    // Setting Schedule ----------------------
    const Booked = { '2020/09/10' : ['08:00-08:30','11:45-12:15']};
    const Horraire = ['08:00','17:15'];
    const StartHour = moment(Horraire[0],"HH:mm");
    const endtHour = moment(Horraire[1],"HH:mm");
    const length = (endtHour.format("HH")-StartHour.format("HH"))*60/45;
    const results = Scheduler(StartHour,endtHour,length);

    // --------------------------------------
    // States -------------------
    const [visible,setvisible] = useState(false);
    const [title , setTitle] = useState();
    // -----------------------
    const datePusher = new Date();
    const dayResult = [];
    dayResult.push(moment(Date.now()).format('YYYY/MM/DD'))
    for (let i = 0 ; i < 6; i++) {
        datePusher.setDate(datePusher.getDate()+1);
        dayResult.push(moment(datePusher).format('YYYY/MM/DD'));
    }
    console.log(dayResult);
    return(
        <table id="customers">
            <Modal title={title} visible={visible} width="50vw" onCancel={()=>setvisible(false)}  footer={[
                            <Button disabled={false} key="submit" type="primary"  style={{borderRadius :'10px', fontFamily :'GlacialBold'}}  >
                                Submit
                            </Button>
                            ]}>Hello</Modal>
            <thead>
            
                <tr>
                    <th></th>
                    {dayResult.map((day,index) =>(<th key={day+Date.now()}  style={parseInt(day.split('/')[2]) === date.getDate() ? {color : '#1a73e8' , textAlign : "center"} : {textAlign : "center"}} >{DatePicker(day)[0]} <br/> {DatePicker(day)[1]} {DatePicker(day)[2]} </th>))}
                </tr>
            </thead>
            <tbody>
                {results.map(hour => (
                    <tr key={hour+Date.now()}> 
                        <th>{hour}</th>
                        {dayResult.map(day =>{Booked[day] !== undefined ?(
                            Booked[day].includes(hour) ?
                            <td key={day+hour} className="Booked">
                            Booked </td> :
                            <td key={day+hour} onClick={()=>{setTitle(hour+" | "+day+" | "); setvisible(true)}} className="Free">
                            </td>
                        ):
                        <td key={day+hour} onClick={()=>{setTitle(hour+" | "+day+" | "); setvisible(true)}} >
                        </td>
                        }
                        )}
                    </tr>
                ))}
            </tbody>
            
        </table>
    )
}
export default calendar ;
