        <div key={props.key} className={props.className}>
            <div className="Card_Head">
                <div className="ProfileImg">
                    <img src={props.profile.Usrimg} />
                </div>
                <div className="ProfileInfo">
                    <h3>{props.profile.companyname}</h3>
                    <p>{props.profile.city}</p>
