import './style.scss';
import Modal from 'antd/lib/modal/Modal';
import {useState} from 'react';
import moment from 'moment';
import '../../styles/ant-picker.scss';
const Scheduler = async (StartHour , endtHour , length )=>{
    const results = [];
     for (let i = 0; i <= parseInt(length) ; i++) {
        const string  =  StartHour.format('HH:mm')+"-"+StartHour.add(30,'minutes').format('HH:mm') ;

        // Break betweeen meetings
         results.push(string);
        if (Math.abs(moment.duration(StartHour.diff(endtHour)).asMinutes()) <= 30 ) {
            break ;
        }
        
        await StartHour.add(15,'minutes');
    }
    return(results);
}

const calendar = ()=>{

    //Data from database 
    const Booked = ['08:00-10:00','10:00-11:00'];
    const Horraire = ['08:00','17:15'];
    const StartHour = moment(Horraire[0],"HH:mm");
    const endtHour = moment(Horraire[1],"HH:mm");
    const length = (endtHour.format("HH")-StartHour.format("HH"))*60/45;
    
    const results = Scheduler(StartHour,endtHour,length);
    console.log(results);
    // States -------------------
    const [visible,setvisible] = useState(false);
    const [title , setTitle] = useState();

    // -----------------------
    const Days = ['Lundi','Mardi','Mercredi','Jeudi','Vendredi','Samedi','Dimanche'];
    const Hours = ["","",""];
    return(
        <table id="customers">
            <Modal title={`${title} | Entrez votre message`} visible={visible} width="50vw" onCancel={()=>setvisible(false)} footer={null}>Hello</Modal>
            <thead>
                <tr>
                    <th></th>
                    {Days.map(day =>(<th key={day+Date.now()} >{day}</th>))}
                </tr>
            </thead>
            <tbody>
                {Hours.map(hour => (
                    <tr key={hour+Date.now()}> 
                        <th>{hour}</th>
                        {Days.map(day =>(
                        <td key={day+hour} onClick={()=>{setTitle(hour+day); setvisible(true)}}>
                        
                        </td>))}
                    </tr>
                ))}
            </tbody>
            
        </table>
    )
}
export default calendar ;
