import Header from '../../components/header';
import Head from '../../components/head';
import axios from 'axios'; 
import {useEffect , useState} from 'react'
import jwt from 'jsonwebtoken';

/*
export async function getStaticProps() {
    
        // Call an external API endpoint to get posts.
    // You can use any data fetching library
    
    const token = window.localStorage.getItem("Tokens");
    var decoded =   jwt.verify(token,"secret",function (err , decoded) {
        if (!err) {
            return decoded.userId ;
        }
    });
    console.log(decoded);
    var url = "http://localhost:9000/profiles/myprofile?userid="+decoded ;
    const res = await fetch(url)
    const posts = await res.json()
    
    // By returning { props: posts }, the Blog component
    // will receive `posts` as a prop at build time
    return {
        props: {
        posts,
        },
    }
}*/
const Profile = ()=>{
    /*const nom = posts.response.profiles[0].nom ; 
    const prenom = posts.response.profiles[0].prenom ; 
    const companyname = posts.response.profiles[0].companyname ; 
    const string = "Bonsoir "+nom+" "+prenom+", Votre societe est "+companyname ;*/
    const [data,setData] = useState();
    const [loading,setLoading] = useState(true);

    useEffect(()=>{
        const token = window.localStorage.getItem("Tokens");
        jwt.verify(token,"secret",function (err , decoded) {
            if (!err) {
                const auth = "Bearer "+token ;
                var url = "http://localhost:9000/profiles/myprofile?userid="+decoded.userId ;
                axios.get(url,{
                    headers: {
                      'Authorization': auth
                    }
                  })
                .then(res=>{
                    console.log(res.data.profile)
                    setLoading(false);
                })
                .catch(error =>{
                    console.log(error);
                })
            }
        });
        
    })
    if (loading) {
        return(
            <div>
                <Header/>
                <Head/>
                LOADING ...
            </div>
        )
    }else{
        return(
        <div>
            <Header/>
            <Head/>
            {console.log(data)}
        </div>
        )
    }
    
}

export default Profile ;
