import Header from '../../components/header';
import Head from '../../components/head';
import Footer from '../../components/footer';
import './scss/tem.scss';
import TemCard from './Components/TemCard';
import {useEffect , useState} from 'react';
import { Skeleton } from 'antd';
const Temoignages = (props)=>{


    const [Data,setData] = useState();
    const [loading,setLoading] = useState(true);
    const [id,setUserID]= useState(null);
    useEffect(()=>{
            console.log(props);
            setData(props.data.data);
            setLoading(false);
    },[])
    console.log(id);
    return (
      <>
        <div className="body">
          <div className="ContentInside">
            <Head title="Témoignages | 6 solutions" />
            <Header active="temoignages" setUserID={setUserID}/>
            <div className="proposBody">
            
              <h1 className="title">Témoignages</h1>
              <div style={{display : 'flex',justifyContent : 'space-between',alignItems : 'baseline'}}>
                  <div className="tems"></div>
                  {id !== null && (
                    <div className="AddTem">
                        <input className="TemButton" type="button" value="Ajouter un témoignage" />
                    </div>
                  )}
                  
              </div>
              <Skeleton loading={loading} active={true} avatar={true} round  className='sk'>
              {Data !== undefined &&(
                <div className="TemCards">
                    {Data.map(temData =><TemCard key={temData.temid} props={temData}/>)}
                </div> 
              )}
              </Skeleton>              
              
            </div>
          </div>
        </div>
        <Footer />
      </>
    );
}
export const getServerSideProps = async (ctx) => {
    try {
        const url = "http://localhost:9000/temoin/" ;
        const res = await fetch(url); 
        const statusCode = res.status;
        const data = await res.json();
        
        if (statusCode !== 200) {
            throw statusCode 
        }
        return  {
            props : {data }
        }
    } 
    catch (error) {
        ctx.res.statusCode = error;
        return {
               props : {statusCode : error}
        };
    }   
  }
export default Temoignages ;
