import '../Sass/editbox.scss';
import {useState } from 'react';
import {Select ,TimePicker , Modal } from 'antd';
import { storage } from '../../Settings/Components/firebase-config'; 


//  --------------

const EditBox = ({profile}) =>{
    
    // States --------------------------------------------------------
    
    const { RangePicker } = TimePicker;
    const { Option } = Select;
    const  [time , setTime] =useState([]);
    const [data , setData] = useState(profile);
    const Languages = ["Arabe","Francais","Anglais","Spanish"];
    const Fields = ["Agriculture"
                    ,"Agroalimentaire"
                    ,"Alimentation"
                    ,"Animaux"
                    ,"Architecture - Aménagement intérieur"
                    ,"Artisanat - Métiers d'art"
                    ,"Banque - Finance - Assurance"
                    ,"Bâtiment - Travaux publics"
                    ,"Biologie - Chimie"
                    ,"Commerce - Immobilier"
                    ,"Communication - Information"
                    ,"Culture - Spectacle"
                    ,"Défense - Sécurité - Secours"
                    ,"Droit"
                    ,"Edition - Imprimerie - Livre"
                    ,"Electronique - Informatique"
                    ,"Enseignement - Formation"
                    ,"Humanitaire"
                    ,"Industrie - Matériaux"
                    ,"Lettres - Sciences humaines"
                    ,"Mécanique - Maintenance"
                    ,"Santé"
                    ,"Sciences - Maths - Physique"
                    ,"Secrétariat - Accueil"
                    ,"Social - Services à la personne"
                    ,"Soins - Esthétique - Coiffure"
                    ,"Sport - Animation "
                    ,"Transport - Logistique"
                  ];
    const lang = [];
    const children = [];
    const [filefirebase,setfire] = useState(null);
    const [changed,setchanged] = useState(false);
    const [DiplomeList , setDiplomeList] = useState([{diplomeName : "",desc : ""}]);
    const [file,setFile] = useState(profile.Usrimg);
    const [visible,setvisible] = useState(false);
    // Mappers -------------------------------------------------------
    Languages.map(Field =>{
        lang.push(<Option key={Field}>{Field}</Option>);
    });
    Fields.map(Field =>{
        children.push(<Option key={Field}>{Field}</Option>);
    })

    // Firebase url upload function 
    const firebaseUrl = (file) =>{
        return new Promise((resolve,reject)=>{
            let currentImageName = "firebase-image-" + Date.now();
            let uploadImage = storage.ref(`images/${currentImageName}`).put(file);
    
            uploadImage.on('state_changed',
                (snapshot) => {
                    var per = Math.floor((snapshot.bytesTransferred / snapshot.totalBytes) * 100)
                    setPrecentage(per);
                 },
                (error) => {
                    reject(error);
                },
                () => {
                    storage.ref('images').child(currentImageName).getDownloadURL().then(url => {
                        const imageData = [url, currentImageName]
                        resolve(imageData);
                    })
                })
        })
    }
    // Handlers --------------------------------------------------------
    const handleChange = (e)=>{
        setchanged(true);
        if (e.target.files[0] !== undefined) {
            try {
                setfire(e.target.files[0]);
                const url =  URL.createObjectURL(e.target.files[0]);
                setFile(url);
            } catch (error) {
                console.error(error);
                alert(error);
            } 
        }
    }
    // ------------------------------------------------
    // When click on delete picture 
    const handleClick = ()=>{
        URL.revokeObjectURL(file);
        setFile(profile.Usrimg);
        setchanged(false);
    }


    // When diplome change value --------------------------
    // ---------------
    const dipChange = (e, index) => {
      const { name, value } = e.target;
      const list = [...DiplomeList];
      list[index][name] = value;
      setDiplomeList(list);
    };

    // WHen add a diplomaa --------------------------------
    const handleAddClick = () => {
      setDiplomeList([...DiplomeList, { diplomeName: "", desc: "" }]);
    };
    // handle click event of the Remove button
    const handleRemoveClick = (index) => {
      const list = [...DiplomeList];
      list.splice(index, 1);
      setDiplomeList(list);
    };

    
    // Languages on change handler ----------------------------
    const handleLang = (value)=>{
            const string = value ;
            setData({...data , languages : string}); 
    }

    // When chage of time handler --------------------------------
    const handleTime =(time,timestring)=>{
      const string = timestring ;
      setTime(time);
    }
   
    // Handler when input value change 
    const handleInputChange = ()=>{

    }

    if (data === undefined) {
        return(null)
    }else
    {
        return(
            <div className="EditBox">
                <div className="ProfileImg">
                    <img src={file} onClick={()=>setvisible(true)} /><br/>
                    <div>
                        <label className="custom-file-upload">
                            <input type="file" id="img" name="img" placeholder="Choisissez photo" accept="image/*" onChange={handleChange}/>
                            <i className="fa fa-upload" aria-hidden="true"></i>   Ajouter photo
                        </label>
                        {changed &&(
                            <label className="Showmodal" onClick={handleClick}>
                            <i class="fa fa-user-times" style={{color : '#be0000'}}></i>
                            </label>
                        )}
                        
                    </div>
                    
                    <Modal title="Image de profile" visible={visible} onCancel={() => setvisible(false)} footer={null}>
                        <img src={file}  width="100%"/>
                    </Modal>
                </div>
                {/* Nom et prenom  ------------------------------------ */}
                <div className="multipleInput">
                    <input className="input controlled user" type="text" placeholder="Nom" name="nom" value={data.nom} />
                    <input className="input controlled user" type="text" placeholder="Prenom" name="prenom" />
                </div>
                {/* --------------------------------------------------- */}
                <div className="multipleInput" >
                    {/* Title  ------------------------------------ */}
                    <select className="title" name="title"  required>
                        <option value="" disabled selected hidden>Titre de travail</option>
                        <option value="Directeur" >Directeur</option>
                        <option value="Cto">CTO</option>
                    </select>
                    {/* Nom de societe  ------------------------------------ */}
                    <input className="input" type="text" placeholder="Nom de société" name="companyname" />
                </div>
                {/* Domaine  ------------------------------------ */}
                <Select mode="tags" style={{ width: '100%' , marginTop : '20px' }} placeholder="Votre domaine " maxTagCount={3} >
                    {children}
                </Select>
                <div className="multipleInput" placeholder="Titre" style={{marginBottom : '10px'}}>
                    {/* City  ------------------------------------ */}
                    <select className="title" name="city"  >
                        <option value="" disabled selected hidden>Votre ville</option>
                        <option value="Casa" >Casablanca</option>
                        <option value="Rabat">Rabat</option>
                        <option value="Agadir">Agadir</option>
                        <option value="Meknes">Meknes</option>
                    </select>
                    {/* address ------------------------------------*/}
                    <input className="input" type="text" placeholder="Addresse"  name="addresse" />     
                </div>
                {/* L'horraire de la société ----------------------------------------------------------------  */}
                <label style={{color : '#318CE7' , fontFamily : 'GlacialBold' , fontSize : '16px'}}>L'horraire de votre société : </label>
                <RangePicker  bordered={false} format='HH:mm'  minuteStep={30} value={time} onChange={handleTime}/><br/><br/>
                {/* ------------------------------------------------------------------------------------------ */}
                
                {/* Presentation ------------------------------------------ */}
                <label style={{color : '#2a2a2a' , fontFamily : 'GlacialBold' , fontSize : '16px'}} >Presentation</label><br/>
                <textarea className="textArea" name="presentation" placeholder="Décrivez votre entreprise" rows="4" /><br/><br/>
                {/*  ------------------------------------------------------------------------------------------ */}
    
                {/* Languages ------------------------------------------ */}
                <label style={{color : '#2a2a2a' , fontFamily : 'GlacialBold' , fontSize : '16px'}} >Langues parlés</label>
                <Select mode="tags" style={{ width: '100%',borderRadius : '10px', marginTop:'5px' , backgroundColor: '#e9f1fa' }} placeholder="Ex: Francais , arabe ..."
                onChange ={handleLang}  maxTagCount={3} >
                    {lang}
                </Select><br/><br/>
                {/*  ------------------------------------------------------------------------------------------ */}  
                
                {/* Diplomes et formations  */}
                <label style={{color : '#2a2a2a' , fontFamily : 'GlacialBold' , fontSize : '16px'}} >Diplôme et formations</label>
                <div className="Diplomes">
                    {DiplomeList.map((element, index) =>(
                        <div className="container">
                            <input className="input diplome" type="text" name="diplomeName" placeholder="Votre Diplome" value={element.diplomeName}  onChange={e => dipChange(e,index)}/>
                            <input className={index !== 0 ?"input desc2":"input desc"} type="text" name="desc" placeholder="Brève description" value={element.desc} onChange={e => dipChange(e,index)}/>
                            {index !== 0 &&(
                                <label className="delete" onClick={() => handleRemoveClick(index)}>
                                    <i class="fa fa-trash" aria-hidden="true" style={{color : '#be0000'}}></i>
                                </label>
                                )}
                        </div>
                        
                    ))}
                    
                    <div className="container ">
                        <button onClick={handleAddClick} className="ajouter">Ajouter</button>
                    </div>
                    
                </div>
    
    
            </div>
        )
    }
    
}
export default EditBox ;
