import '../styles/bodySection2.scss'
import SearchCard from './SearchCard';
import {useState , useEffect} from 'react';

const BodySection2 = (props)=>{
    
  const [profile , setData] = useState();
  const [loading , setLoading] = useState(true); 
    console.log(props)
    useEffect(()=>{
        if (props.data === undefined) {
          setData(props.data.profile);
          setLoading(false);
        }
    },[])

    if (loading) {
      return null ;
    } else {
      return (
        <div className="bodySection2">
          <div className="titleContainer">
            <h1>Quelques entreprise les plus connues</h1>
          </div>
          <div className="CardsContainer">
            <SearchCard className="Card1" profile={profile[0]}/>
            <div className="row2">
              <SearchCard className="Card1 second" profile={profile[1]}/>
            </div>
            <div className="row2">
              <SearchCard className="Card1 third" profile={profile[2]}/>
            </div>
          </div>
        </div>
        )
    }
}
export const getServerSideProps = async (ctx) => {

      const url = "http://localhost:9000/profiles/hotprofiles" ;
      const res = await fetch( url); 
      const data = await res.json();
    
      return  {
          props : {data }
      }
  
}
export default BodySection2 ;
